CREATE TABLE `chucvu` (
  `IDChucVu` int(11) NOT NULL AUTO_INCREMENT,
  `MoTa` varchar(250) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`IDChucVu`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='bảng này để biết chức vụ của giáo viên';

CREATE TABLE `khoi` (
  `maKhoi` int(11) NOT NULL AUTO_INCREMENT,
  `moTa` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Khối lớp',
  PRIMARY KEY (`maKhoi`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

CREATE TABLE `monhoc` (
  `maMonHoc` int(11) NOT NULL AUTO_INCREMENT,
  `tenMonHoc` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`maMonHoc`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

CREATE TABLE `thanhpho` (
  `maThanhPho` int(11) NOT NULL AUTO_INCREMENT,
  `tenThanhPho` varchar(250) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`maThanhPho`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='Quản Lý Thành Phố';
